body {
  font-family: Arial, sans-serif;
  padding: 20px;
  display: flex;
  flex-wrap: wrap;
  gap: 30px;
  background: #1a1a2e;
  align-items: center;
}

.glow-btn-1 {
  padding: 12px 24px;
  background: linear-gradient(45deg, #ff006e, #fb5607, #ffbe0b);
  border: none;
  border-radius: 8px;
  color: white;
  font-weight: bold;
  cursor: pointer;
  position: relative;
  overflow: hidden;
  transition: all 0.3s ease;
}
.glow-btn-1::before {
  content: '';
  position: absolute;
  top: -2px;
  left: -2px;
  right: -2px;
  bottom: -2px;
  background: linear-gradient(45deg, #ff006e, #fb5607, #ffbe0b);
  border-radius: 10px;
  z-index: -1;
  filter: blur(8px);
  opacity: 0;
  transition: opacity 0.3s ease;
}
.glow-btn-1:hover::before {
  opacity: 0.8;
}
.glow-btn-1:hover {
  transform: translateY(-2px);
  box-shadow: 0 10px 25px rgba(255, 0, 110, 0.4);
}

.glow-btn-2 {
  padding: 12px 24px;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  border: 2px solid transparent;
  border-radius: 12px;
  color: white;
  font-weight: bold;
  cursor: pointer;
  position: relative;
  transition: all 0.4s ease;
}
.glow-btn-2:hover {
  box-shadow: 0 0 20px rgba(102, 126, 234, 0.6), 0 0 40px rgba(118, 75, 162, 0.4), inset 0 0 20px rgba(255, 255, 255, 0.1);
  transform: scale(1.05);
}

.glow-btn-3 {
  padding: 12px 24px;
  background: linear-gradient(45deg, #56ab2f, #a8e6cf);
  border: none;
  border-radius: 25px;
  color: white;
  font-weight: bold;
  cursor: pointer;
  position: relative;
  animation: pulse-glow 2s infinite;
}
