  0%, 100% {
    box-shadow: 0 0 10px rgba(86, 171, 47, 0.5);
  }
  50% {
    box-shadow: 0 0 25px rgba(86, 171, 47, 0.8), 0 0 50px rgba(168, 230, 207, 0.3);
