js:
function copyText(button, text) {
  navigator.clipboard.writeText(text).then(function() {
    const originalText = button.textContent || button.innerHTML;
    button.classList.add('copied');

    if (button.classList.contains('copy-btn-1')) {
      button.textContent = '✓';
      button.classList.add('show-tooltip');
      button.setAttribute('data-tooltip', 'Copied!');
    } else if (button.classList.contains('copy-btn-2')) {
      button.innerHTML = '<span>✓</span> Copied!';
    } else if (button.classList.contains('copy-btn-3')) {
      button.textContent = 'Copied!';
    } else if (button.classList.contains('copy-btn-4')) {
      button.textContent = '✓ Copied!';
    } else if (button.classList.contains('copy-btn-5')) {
      button.textContent = '✓ Copied!';
    } else if (button.classList.contains('copy-btn-6')) {
      button.textContent = 'copied!';
    }

    setTimeout(function() {
      button.classList.remove('copied');
      button.innerHTML = originalText;
      if (button.classList.contains('copy-btn-1')) {
        button.classList.remove('show-tooltip');
        button.setAttribute('data-tooltip', 'Copy');
      }
    }, 2000);

  }).catch(function(err) {
    console.error('Failed to copy text: ', err);
    const textArea = document.createElement('textarea');
    textArea.value = text;
    document.body.appendChild(textArea);
    textArea.select();

    try {
      document.execCommand('copy');
      button.classList.add('copied');
      const originalText = button.textContent;
      button.textContent = 'Copied!';
      setTimeout(function() {
        button.classList.remove('copied');
        button.textContent = originalText;
      }, 2000);
    } catch (fallbackErr) {
      console.error('Fallback copy failed: ', fallbackErr);
    }

    document.body.removeChild(textArea);
  });
}
