body {
  font-family: Arial, sans-serif;
  padding: 50px;
  background-color: #f5f5f5;
}

.loading-btn-spinner,
.loading-btn-dots,
.loading-btn-progress {
  color: white;
  border: none;
  padding: 14px 28px;
  font-size: 16px;
  border-radius: 6px;
  cursor: pointer;
  margin: 10px;
  transition: background-color 0.3s ease;
  min-width: 140px;
}

.loading-btn-spinner {
  background-color: #3b82f6;
  position: relative;
}
.loading-btn-spinner:hover:not(:disabled) {
  background-color: #2563eb;
}

.loading-btn-dots {
  background-color: #10b981;
}
.loading-btn-dots:hover:not(:disabled) {
  background-color: #059669;
}

.loading-btn-progress {
  background-color: #f59e0b;
  position: relative;
  overflow: hidden;
}
.loading-btn-progress:hover:not(:disabled) {
  background-color: #d97706;
}

.loading-btn-spinner:disabled,
.loading-btn-dots:disabled,
.loading-btn-progress:disabled {
  cursor: not-allowed;
  opacity: 0.7;
}

.spinner {
  width: 16px;
  height: 16px;
  border: 2px solid rgba(255, 255, 255, 0.3);
  border-radius: 50%;
  border-top: 2px solid white;
  animation: spin 1s linear infinite;
  display: inline-block;
  margin-right: 8px;
}

@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

.dots::after {
  content: '';
  animation: dots 1.5s steps(4, end) infinite;
}

@keyframes dots {
  0%, 20% { content: ''; }
  40% { content: '.'; }
  60% { content: '..'; }
  80%, 100% { content: '...'; }
}

.progress-bar::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, rgba(255,255,255,0.2), transparent);
  animation: progress 2s ease-in-out infinite;
}

@keyframes progress {
  0% { left: -100%; }
  50% { left: 100%; }
  100% { left: 100%; }
}

js:
function toggleSpinnerLoading(button) {
  const isLoading = button.disabled;
  if (!isLoading) {
    button.disabled = true;
    button.innerHTML = '<span class="spinner"></span>Loading...';
    setTimeout(() => {
      button.disabled = false;
      button.innerHTML = '<span class="btn-text">Submit Form</span>';
    }, 3000);
  }
}

function toggleDotsLoading(button) {
  const isLoading = button.disabled;
  if (!isLoading) {
    button.disabled = true;
    button.classList.add('dots');
    button.innerHTML = 'Loading<span class="dots"></span>';
    setTimeout(() => {
      button.disabled = false;
      button.classList.remove('dots');
      button.innerHTML = '<span class="btn-text">Save Data</span>';
    }, 3000);
  }
}

function toggleProgressLoading(button) {
  const isLoading = button.disabled;
  if (!isLoading) {
    button.disabled = true;
    button.classList.add('progress-bar');
    button.innerHTML = 'Processing...';
    setTimeout(() => {
      button.disabled = false;
      button.classList.remove('progress-bar');
      button.innerHTML = '<span class="btn-text">Upload File</span>';
    }, 4000);
  }
}

