* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    min-height: 100vh;
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 20px;
}

.flip-card {
    background-color: transparent;
    width: 320px;
    height: 400px;
    perspective: 1000px;
    cursor: pointer;
}

.flip-card-inner {
    position: relative;
    width: 100%;
    height: 100%;
    text-align: center;
    transition: transform 0.6s;
    transform-style: preserve-3d;
}

.flip-card.flipped .flip-card-inner {
    transform: rotateY(180deg);
}

.flip-card-front, .flip-card-back {
    position: absolute;
    width: 100%;
    height: 100%;
    -webkit-backface-visibility: hidden;
    backface-visibility: hidden;
    border-radius: 20px;
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.2);
    padding: 40px 30px;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
}

.flip-card-front {
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    color: white;
}

.flip-card-back {
    background: linear-gradient(135deg, #f093fb 0%, #f5576c 100%);
    color: white;
    transform: rotateY(180deg);
}

.card-icon {
    font-size: 60px;
    margin-bottom: 20px;
}

.card-title {
    font-size: 28px;
    font-weight: 700;
    margin-bottom: 16px;
}

.card-subtitle {
    font-size: 16px;
    opacity: 0.9;
    margin-bottom: 20px;
}

.card-description {
    font-size: 16px;
    line-height: 1.6;
    opacity: 0.9;
}

.flip-hint {
    position: absolute;
    bottom: 20px;
    font-size: 12px;
    opacity: 0.7;
}

@media (max-width: 480px) {
    .flip-card {
        width: 280px;
        height: 360px;
    }

    .flip-card-front, .flip-card-back {
        padding: 32px 24px;
    }

    .card-icon {
        font-size: 50px;
    }

    .card-title {
        font-size: 24px;
    }

    .card-subtitle, .card-description {
        font-size: 14px;
    }
}

