}

body {
  font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
  background: linear-gradient(135deg, #74b9ff 0%, #0984e3 100%);
  min-height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 20px;
}

.expandable-card {
  background: white;
  border-radius: 20px;
  max-width: 400px;
  width: 100%;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
  overflow: hidden;
  transition: all 0.4s cubic-bezier(0.25, 0.8, 0.25, 1);
  cursor: pointer;
}

.card-header {
  padding: 32px;
  text-align: center;
  position: relative;
}

.card-icon {
  width: 80px;
  height: 80px;
  background: linear-gradient(135deg, #74b9ff, #0984e3);
  border-radius: 50%;
  margin: 0 auto 20px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 36px;
  color: white;
}

.card-title {
  font-size: 24px;
  font-weight: 700;
  color: #2d3748;
  margin-bottom: 8px;
}

.card-summary {
  font-size: 16px;
  color: #718096;
  line-height: 1.5;
}

.expand-indicator {
  position: absolute;
  bottom: 16px;
  right: 24px;
  width: 32px;
  height: 32px;
  background: #f7fafc;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.3s ease;
  font-size: 16px;
  color: #4a5568;
}

.expandable-card.expanded .expand-indicator {
  transform: rotate(180deg);
  background: #e2e8f0;
}

.card-content {
  max-height: 0;
  overflow: hidden;
  transition: all 0.4s cubic-bezier(0.25, 0.8, 0.25, 1);
  background: #f8fafc;
}

.expandable-card.expanded .card-content {
  max-height: 400px;
}

.card-details {
  padding: 32px;
}

.detail-item {
  margin-bottom: 20px;
}

.detail-label {
  font-size: 14px;
  font-weight: 600;
  color: #4a5568;
  margin-bottom: 4px;
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

.detail-value {
  font-size: 16px;
  color: #2d3748;
  line-height: 1.5;
}

.card-actions {
  padding: 24px 32px;
  border-top: 1px solid #e2e8f0;
  display: flex;
  gap: 12px;
}

.btn {
  flex: 1;
  padding: 12px 20px;
  border: none;
  border-radius: 8px;
  font-size: 14px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
}

.btn-primary {
  background: linear-gradient(135deg, #74b9ff, #0984e3);
  color: white;
}

.btn-primary:hover {
  transform: translateY(-2px);
  box-shadow: 0 5px 15px rgba(116, 185, 255, 0.4);
}

.btn-secondary {
  background: #e2e8f0;
  color: #4a5568;
}

.btn-secondary:hover {
  background: #cbd5e0;
}

@media (max-width: 480px) {
  .card-header {
    padding: 24px 20px;
  }

  .card-details {
    padding: 24px 20px;
  }

  .card-actions {
    padding: 20px;
    flex-direction: column;
  }

  .card-icon {
    width: 60px;
    height: 60px;
    font-size: 28px;
  }

  .card-title {
    font-size: 20px;
  }

  .card-summary {
    font-size: 14px;
  }
}

js:
// Expandable card functionality
const expandableCard = document.getElementById('expandableCard');

expandableCard.addEventListener('click', function(e) {
  // Don't expand if clicking on buttons
  if (e.target.classList.contains('btn')) {
    return;
  }
  this.classList.toggle('expanded');
});

// Handle button clicks
const buttons = document.querySelectorAll('.btn');
buttons.forEach(button => {
  button.addEventListener('click', function(e) {
    e.stopPropagation(); // Prevent card expansion
    if (this.classList.contains('btn-primary')) {
      alert('Get Started clicked!');
    } else {
      alert('Learn More clicked!');
    }
  });
});

// Keyboard accessibility
expandableCard.addEventListener('keydown', function(e) {
  if (e.key === 'Enter' || e.key === ' ') {
    e.preventDefault();
    this.classList.toggle('expanded');
  }
});

// Make it focusable
expandableCard.setAttribute('tabindex', '0');

