* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    min-height: 260px;
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 20px;
}

.flip-card {
    background-color: transparent;
    width: 460px;
    height: 220px;
    perspective: 1000px;
    cursor: pointer;
    transition: transform 0.2s ease;
}

.flip-card:hover {
    transform: scale(1.02);
}

.flip-card-inner {
    position: relative;
    width: 100%;
    height: 100%;
    text-align: center;
    transition: transform 0.8s cubic-bezier(0.175, 0.885, 0.32, 1.275);
    transform-style: preserve-3d;
}

.flip-card.flipped .flip-card-inner {
    transform: rotateY(180deg);
}

.flip-card-front, .flip-card-back {
    position: absolute;
    width: 100%;
    height: 100%;
    -webkit-backface-visibility: hidden;
    backface-visibility: hidden;
    border-radius: 16px;
    box-shadow: 0 15px 35px rgba(0, 0, 0, 0.15), 0 5px 15px rgba(0, 0, 0, 0.1);
    padding: 20px;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    backdrop-filter: blur(10px);
}

.flip-card-front {
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    color: white;
}

.flip-card-back {
    background: linear-gradient(135deg, #f093fb 0%, #f5576c 100%);
    color: white;
    transform: rotateY(180deg);
}

.card-icon {
    font-size: 32px;
    margin-bottom: 12px;
    filter: drop-shadow(0 2px 4px rgba(0, 0, 0, 0.2));
}

.card-title {
    font-size: 22px;
    font-weight: 700;
    margin-bottom: 8px;
}

.card-subtitle {
    font-size: 14px;
    opacity: 0.9;
    margin-bottom: 12px;
}

.card-description {
    font-size: 13px;
    line-height: 1.5;
    opacity: 0.9;
}

.flip-hint {
    position: absolute;
    bottom: 12px;
    font-size: 11px;
    opacity: 0.7;
}

@media (max-width: 520px) {
    .flip-card {
        width: calc(100% - 40px);
        height: 200px;
    }

    .flip-card-front, .flip-card-back {
        padding: 18px;
    }

    .card-icon {
        font-size: 28px;
    }

    .card-title {
        font-size: 20px;
    }

    .card-subtitle, .card-description {
        font-size: 12px;
    }
}

