css:
body {
  margin: 0;
  padding: 20px;
  font-family: 'Arial', sans-serif;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  min-height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  overflow: hidden;
}

.flip-container {
  perspective: 1000px;
  width: 300px;
  height: 200px;
  overflow: hidden;
}

.flip-card {
  position: relative;
  width: 100%;
  height: 100%;
  transform-style: preserve-3d;
  transition: transform 0.6s;
  animation: horizontalFlip 3s infinite;
}

.flip-card-front,
.flip-card-back {
  position: absolute;
  width: 100%;
  height: 100%;
  backface-visibility: hidden;
  border-radius: 15px;
  display: flex;
  justify-content: center;
  align-items: center;
  color: white;
  font-size: 24px;
  font-weight: bold;
  box-shadow: 0 8px 32px rgba(0,0,0,0.3);
}

.flip-card-front {
  background: linear-gradient(45deg, #ff6b6b, #feca57);
}

.flip-card-back {
  background: linear-gradient(45deg, #48cae4, #023e8a);
  transform: rotateX(180deg);
}

@keyframes horizontalFlip {
  0%, 40% { transform: rotateX(0deg); }
  60%, 100% { transform: rotateX(180deg); }
}

@media (max-width: 480px) {
  .flip-container {
    width: 250px;
    height: 160px;
  }
  .flip-card-front, .flip-card-back {
    font-size: 18px;
  }
}

