const searchInput = document.getElementById('searchInput');
const suggestions = document.getElementById('suggestions');
const searchForm = document.getElementById('searchForm');

// Sample product data
const products = ['Apple iPhone', 'Samsung Galaxy', 'iPad Pro', 'MacBook Air', 'Dell Laptop', 'HP Printer', 'Sony Headphones', 'Canon Camera', 'Nike Shoes', 'Adidas Jacket', 'Coffee Maker', 'Bluetooth Speaker', 'Gaming Mouse', 'Wireless Keyboard', 'Smart Watch'];

searchInput.addEventListener('input', function() {
  const query = this.value.toLowerCase().trim();

  if (query === '') {
    suggestions.classList.remove('show');
    return;
  }

  const matches = products.filter(product => 
    product.toLowerCase().includes(query)
  ).slice(0, 5);

  if (matches.length > 0) {
    suggestions.innerHTML = matches.map(match => 
      `<li class="suggestion-item" onclick="selectSuggestion('${match}')">${match}</li>`
    ).join('');
    suggestions.classList.add('show');
  } else {
    suggestions.classList.remove('show');
  }
});

function selectSuggestion(value) {
  searchInput.value = value;
  suggestions.classList.remove('show');
}

// Hide suggestions when clicking outside
document.addEventListener('click', function(e) {
  if (!searchForm.contains(e.target)) {
    suggestions.classList.remove('show');
  }
});

searchForm.addEventListener('submit', function(e) {
  e.preventDefault();
  alert(`Searching for: ${searchInput.value}`);
  suggestions.classList.remove('show');
});

