js:
const stars = document.querySelectorAll('.star');
const ratingInput = document.getElementById('rating');
const surveyForm = document.getElementById('surveyForm');

// Star rating functionality
stars.forEach((star, index) => {
  star.addEventListener('click', function() {
    const rating = index + 1;
    ratingInput.value = rating;

    // Fill stars up to the clicked one
    stars.forEach((s, i) => {
      if (i < rating) {
        s.classList.add('filled');
        s.textContent = '‚òÖ'; // Filled star
      } else {
        s.classList.remove('filled');
        s.textContent = '‚òÜ'; // Empty star
      }
    });
  });

  // Hover effect - preview the rating
  star.addEventListener('mouseenter', function() {
    stars.forEach((s, i) => {
      if (i <= index) {
        s.style.color = '#d4a574';
        s.style.transform = 'scale(1.2)';
        s.textContent = '‚òÖ'; // Show filled star on hover
      } else {
        s.style.color = '#ddd';
        s.style.transform = 'scale(1)';
        s.textContent = '‚òÜ'; // Show empty star on hover
      }
    });
  });
});

// Reset to actual rating when mouse leaves star area
document.querySelector('.rating-stars').addEventListener('mouseleave', function() {
  const currentRating = parseInt(ratingInput.value) || 0;

  stars.forEach((star, index) => {
    if (index < currentRating) {
      star.style.color = '#d4a574';
      star.style.transform = 'scale(1.1)';
      star.textContent = '‚òÖ';
      star.classList.add('filled');
    } else {
      star.style.color = '#ddd';
      star.style.transform = 'scale(1)';
      star.textContent = '‚òÜ';
      star.classList.remove('filled');
    }
  });
});

// Form submission
surveyForm.addEventListener('submit', function(e) {
  e.preventDefault();

  if (!ratingInput.value) {
    alert('Please select a rating!');
    return;
  }

  const formData = new FormData(this);
  const data = {
    rating: formData.get('rating'),
    source: formData.get('source'),
    comments: formData.get('comments')
  };

  alert(`Thank you for your feedback!\nRating: ${data.rating} stars\nSource: ${data.source}\nComments: ${data.comments || 'None'}`);

  // Reset form
  this.reset();
  ratingInput.value = '';
  stars.forEach(star => {
    star.classList.remove('filled');
    star.textContent = '‚òÜ';
    star.style.color = '#ddd';
    star.style.transform = 'scale(1)';
  });
});

