const detectLocationBtn = document.getElementById('detectLocation');
const coordinatesDiv = document.getElementById('coordinates');
const addressForm = document.getElementById('addressForm');
const streetInput = document.getElementById('street');
const cityInput = document.getElementById('city');
const postalInput = document.getElementById('postal');

// Detect location functionality
detectLocationBtn.addEventListener('click', function() {
  if ('geolocation' in navigator) {
    this.textContent = 'üìç Detecting...';
    this.disabled = true;

    navigator.geolocation.getCurrentPosition(
      function(position) {
        const lat = position.coords.latitude.toFixed(6);
        const lng = position.coords.longitude.toFixed(6);

        coordinatesDiv.innerHTML = `üìç Location: ${lat}, ${lng}`;
        coordinatesDiv.classList.add('show');

        // Simulate reverse geocoding with mock data
        setTimeout(() => {
          streetInput.value = '123 Main Street';
          cityInput.value = 'Your City';
          postalInput.value = '12345';

          detectLocationBtn.textContent = '‚úÖ Location Detected';
          detectLocationBtn.disabled = false;
        }, 1000);
      },
      function(error) {
        let errorMsg = 'Location access denied';
        switch(error.code) {
          case error.PERMISSION_DENIED:
            errorMsg = 'Location access denied by user';
            break;
          case error.POSITION_UNAVAILABLE:
            errorMsg = 'Location information unavailable';
            break;
          case error.TIMEOUT:
            errorMsg = 'Location request timeout';
            break;
        }

        coordinatesDiv.innerHTML = `‚ùå ${errorMsg}`;
        coordinatesDiv.classList.add('show');
        detectLocationBtn.textContent = 'üìç Try Again';
        detectLocationBtn.disabled = false;
      },
      {
        enableHighAccuracy: true,
        timeout: 10000,
        maximumAge: 60000
      }
    );
  } else {
    coordinatesDiv.innerHTML = '‚ùå Geolocation not supported';
    coordinatesDiv.classList.add('show');
  }
});

// Form submission
addressForm.addEventListener('submit', function(e) {
  e.preventDefault();

  const formData = new FormData(this);
  const addressData = {
    street: formData.get('street'),
    city: formData.get('city'),
    postal: formData.get('postal'),
    country: formData.get('country')
  };

  alert(`Address Saved!\n${addressData.street}\n${addressData.city}, ${addressData.postal}\n${addressData.country}`);
});
