const form = document.getElementById('validationForm');
const email = document.getElementById('email');
const password = document.getElementById('password');
const confirmPassword = document.getElementById('confirmPassword');

email.addEventListener('input', function() {
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  const errorMsg = document.getElementById('emailError');
  const successMsg = document.getElementById('emailSuccess');

  if (emailRegex.test(this.value)) {
    this.classList.remove('invalid');
    this.classList.add('valid');
    errorMsg.classList.remove('show');
    successMsg.classList.add('show');
  } else {
    this.classList.remove('valid');
    this.classList.add('invalid');
    errorMsg.classList.add('show');
    successMsg.classList.remove('show');
  }
});

password.addEventListener('input', function() {
  const errorMsg = document.getElementById('passwordError');
  const successMsg = document.getElementById('passwordSuccess');

  if (this.value.length >= 6) {
    this.classList.remove('invalid');
    this.classList.add('valid');
    errorMsg.classList.remove('show');
    successMsg.classList.add('show');
  } else {
    this.classList.remove('valid');
    this.classList.add('invalid');
    errorMsg.classList.add('show');
    successMsg.classList.remove('show');
  }

  if (confirmPassword.value) {
    confirmPassword.dispatchEvent(new Event('input'));
  }
});

confirmPassword.addEventListener('input', function() {
  const errorMsg = document.getElementById('confirmError');
  const successMsg = document.getElementById('confirmSuccess');

  if (this.value === password.value && password.value.length >= 6) {
    this.classList.remove('invalid');
    this.classList.add('valid');
    errorMsg.classList.remove('show');
    successMsg.classList.add('show');
  } else {
    this.classList.remove('valid');
    this.classList.add('invalid');
    errorMsg.classList.add('show');
    successMsg.classList.remove('show');
  }
});

form.addEventListener('submit', function(e) {
  e.preventDefault();

  const allValid = email.classList.contains('valid') &&
                  password.classList.contains('valid') &&
                  confirmPassword.classList.contains('valid');

  if (allValid) {
    alert('Registration successful!');
  } else {
    alert('Please fix all errors before submitting');
  }
});

