// JavaScript logic placeholder
const form = document.getElementById('progressForm');
const steps = document.querySelectorAll('.form-step');
const nextBtn = document.getElementById('nextBtn');
const prevBtn = document.getElementById('prevBtn');
const submitBtn = document.querySelector('.btn-submit');
const progressLine = document.getElementById('progressLine');
const stepsIndicator = document.querySelectorAll('.step');
const percentText = document.getElementById('progressPercent');
const completionMessage = document.getElementById('completionMessage');

let currentStep = 0;

function updateFormSteps() {
  steps.forEach((step, i) => step.classList.toggle('active', i === currentStep));
  stepsIndicator.forEach((s, i) => {
    s.classList.remove('active', 'completed');
    if (i < currentStep) s.classList.add('completed');
    else if (i === currentStep) s.classList.add('active');
  });
  const progressPercent = ((currentStep + 1) / steps.length) * 100;
  progressLine.style.width = `${progressPercent}%`;
  percentText.textContent = `${Math.round(progressPercent)}% Complete`;
  prevBtn.disabled = currentStep === 0;
  nextBtn.style.display = currentStep < steps.length - 1 ? 'inline-block' : 'none';
  submitBtn.style.display = currentStep === steps.length - 1 ? 'inline-block' : 'none';
}

nextBtn.addEventListener('click', () => {
  if (currentStep < steps.length - 1) currentStep++;
  updateFormSteps();
});

prevBtn.addEventListener('click', () => {
  if (currentStep > 0) currentStep--;
  updateFormSteps();
});

form.addEventListener('submit', e => {
  e.preventDefault();
  form.style.display = 'none';
  completionMessage.classList.add('show');
});

updateFormSteps();
