css:
body {
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
  height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 0;
  padding: 10px;
  box-sizing: border-box;
}

.container {
  width: min(500px, 90vw);
  height: min(260px, 50vh);
  background: #fafafa;
  border-radius: 12px;
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
  box-shadow: 0 8px 32px rgba(0,0,0,0.08), 0 4px 16px rgba(0,0,0,0.04);
  border: 1px solid #e8e8e8;
}

.open-btn {
  padding: 14px 28px;
  background: #4a4a4a;
  color: white;
  border: none;
  border-radius: 8px;
  font-size: 15px;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  box-shadow: 0 4px 12px rgba(74, 74, 74, 0.2);
}

.open-btn:hover {
  background: #3a3a3a;
  transform: translateY(-2px) scale(1.01);
  box-shadow: 0 6px 20px rgba(74, 74, 74, 0.25);
}

.open-btn:active {
  transform: translateY(0) scale(0.98);
}

/* Modal Overlay */
.modal-overlay {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.4);
  display: none;
  align-items: center;
  justify-content: center;
  z-index: 1000;
  backdrop-filter: blur(4px);
  opacity: 0;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.modal-overlay.active {
  display: flex;
  opacity: 1;
}

/* Modal Content */
.modal {
  background: white;
  padding: 32px 28px;
  border-radius: 16px;
  text-align: center;
  max-width: min(320px, 80vw);
  width: 85%;
  position: relative;
  box-shadow: 0 20px 60px rgba(0,0,0,0.15), 0 8px 24px rgba(0,0,0,0.08);
  transform: scale(0.8) translateY(30px);
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  border: 1px solid #f0f0f0;
}

.modal-overlay.active .modal {
  transform: scale(1) translateY(0);
}

.close-btn {
  position: absolute;
  top: 12px;
  right: 16px;
  background: #f5f5f5;
  border: none;
  font-size: 24px;
  cursor: pointer;
  color: #666;
  width: 32px;
  height: 32px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.2s ease;
}

.close-btn:hover {
  background: #e8e8e8;
  color: #333;
  transform: scale(1.05);
}

.modal h2 {
  margin-bottom: 16px;
  color: #2a2a2a;
  font-weight: 600;
  font-size: clamp(1.1rem, 4vw, 1.3rem);
}

.modal p {
  color: #5a5a5a;
  line-height: 1.5;
  font-size: clamp(0.85rem, 3vw, 0.95rem);
}

@media (max-width: 480px) {
  .modal {
    padding: 24px 20px;
    margin: 20px;
    max-width: min(280px, 85vw);
  }
  .container {
    height: min(200px, 40vh);
  }
  .open-btn {
    padding: 12px 24px;
    font-size: 14px;
  }
}

