    backdrop-filter: blur(4px);
}

.modal-overlay.active {
    display: block;
}

/* Draggable Modal */
.modal {
    position: absolute;
    background: #ffffff;
    border-radius: 12px;
    width: 240px;
    max-width: 85vw;
    box-shadow: 0 20px 40px rgba(15, 23, 42, 0.15);
    overflow: hidden;
    cursor: move;
    transition: all 0.3s ease;
    border: 1px solid #e2e8f0;
}

.modal-header {
    background: linear-gradient(135deg, #64748b, #475569);
    padding: 12px 16px;
    cursor: grab;
    user-select: none;
    display: flex;
    justify-content: space-between;
    align-items: center;
    border-bottom: 1px solid #e2e8f0;
}

.modal-header:active {
    cursor: grabbing;
}

.modal-title {
    font-size: 13px;
    font-weight: 600;
    color: white;
}

.close-btn {
    background: none;
