js:
let isDragging = false;
let currentX;
let currentY;
let initialX;
let initialY;
let xOffset = 0;
let yOffset = 0;

const modal = document.getElementById('draggableModal');
const modalHeader = document.getElementById('modalHeader');

function openModal() {
    document.getElementById('modalOverlay').classList.add('active');
    // Reset position to center
    modal.style.transform = 'translate(-50%, -50%)';
    modal.style.left = '50%';
    modal.style.top = '50%';
    xOffset = 0;
    yOffset = 0;
}

function closeModal() {
    document.getElementById('modalOverlay').classList.remove('active');
    isDragging = false;
    document.body.classList.remove('dragging');
}

// Mouse events for dragging
modalHeader.addEventListener('mousedown', dragStart);
document.addEventListener('mousemove', drag);
document.addEventListener('mouseup', dragEnd);

// Touch events for mobile
modalHeader.addEventListener('touchstart', dragStart);
document.addEventListener('touchmove', drag);
document.addEventListener('touchend', dragEnd);

function dragStart(e) {
    if (e.type === "touchstart") {
        initialX = e.touches[0].clientX - xOffset;
        initialY = e.touches[0].clientY - yOffset;
    } else {
        initialX = e.clientX - xOffset;
        initialY = e.clientY - yOffset;
    }

    if (e.target === modalHeader || modalHeader.contains(e.target)) {
        isDragging = true;
        document.body.classList.add('dragging');

        // Remove centering transform
        modal.classList.remove('modal-centered');

        // Get current position
        const rect = modal.getBoundingClientRect();
        modal.style.left = rect.left + 'px';
        modal.style.top = rect.top + 'px';
        modal.style.transform = 'none';
    }
}

function drag(e) {
    if (isDragging) {
        e.preventDefault();

        if (e.type === "touchmove") {
            currentX = e.touches[0].clientX - initialX;
            currentY = e.touches[0].clientY - initialY;
        } else {
            currentX = e.clientX - initialX;
            currentY = e.clientY - initialY;
        }

        xOffset = currentX;
        yOffset = currentY;

        // Constrain to viewport
        const modalRect = modal.getBoundingClientRect();
        const maxX = window.innerWidth - modalRect.width;
        const maxY = window.innerHeight - modalRect.height;

        const newX = Math.max(0, Math.min(maxX, parseFloat(modal.style.left) + currentX - xOffset + xOffset));
        const newY = Math.max(0, Math.min(maxY, parseFloat(modal.style.top) + currentY - yOffset + yOffset));

        modal.style.left = newX + 'px';
        modal.style.top = newY + 'px';
    }
}

function dragEnd() {
    isDragging = false;
    document.body.classList.remove('dragging');
    initialX = currentX;
    initialY = currentY;
}

// Close modal on escape key
document.addEventListener('keydown', function(e) {
    if (e.key === 'Escape') {
        closeModal();
    }
});

// Prevent context menu on drag header
modalHeader.addEventListener('contextmenu', function(e) {
    e.preventDefault();
});

