    transform: translateX(100%);
    transition: transform 0.4s cubic-bezier(0.25, 0.8, 0.25, 1);
    box-shadow: -10px 0 30px rgba(0,0,0,0.1);
}

.modal-overlay.active .modal-right {
    transform: translateX(0);
}

/* Modal Header */
.modal-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 25px;
    padding-bottom: 15px;
    border-bottom: 2px solid #f1f3f4;
}

.modal-title {
    font-size: 24px;
    font-weight: 600;
    color: #333;
}

.close-btn {
    background: none;
    border: none;
    font-size: 24px;
    cursor: pointer;
    color: #999;
    transition: all 0.3s ease;
    padding: 5px;
    border-radius: 50%;
}

.close-btn:hover {
    color: #333;
    background: #f1f3f4;
}

/* Modal Content */
.modal-content h3 {
    margin-bottom: 15px;
    color: #333;
    font-size: 18px;
}

.modal-content p {
    color: #666;
    line-height: 1.8;
    margin-bottom: 20px;
}

.feature-list {
    list-style: none;
    margin: 20px 0;
}

.feature-list li {
    padding: 10px 0;
    color: #555;
    border-bottom: 1px solid #f1f3f4;
}

.feature-list li:before {
    content: "‚úì";
    color: #4caf50;
    font-weight: bold;
    margin-right: 10px;
}

.modal-actions {
    margin-top: 30px;
}

.btn-primary {
    background: linear-gradient(45deg, #667eea, #764ba2);
    color: white;
    padding: 12px 25px;
    border: none;
    border-radius: 8px;
    cursor: pointer;
    font-size: 14px;
    transition: all 0.3s ease;
}

.btn-primary:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(102,126,234,0.4);
}

@media (max-width: 768px) {
    .container {
        width: 90vw;
        height: 40vh;
        flex-direction: column;
    }
    .modal-right {
        width: 100vw;
    }
}

